html.script:
function resize() {

   var w_newWidth,w_newHeight;
   var w_maxWidth=2000, w_maxHeight=1200, w_staticHeight=550;


   if (navigator.appName.indexOf("Microsoft") != -1)
   {

     w_newWidth=document.body.clientWidth;
     w_newHeight=document.body.clientHeight-1;
     //alert("iexplorer resize - width:"+w_newWidth+" height:"+w_staticHeight+" Applet - width:"+document.applets["xmlEditor"].getWidth());           

   }else{
   var netscapeScrollWidth=15;
   w_newWidth=window.innerWidth-netscapeScrollWidth;
   w_newHeight=window.innerHeight-netscapeScrollWidth;
   if (w_newWidth>w_maxWidth)
     w_newWidth=w_maxWidth;

   if (w_newHeight>w_maxHeight)
     w_newHeight=w_maxHeight;
   document.applets["xmlEditor"].setSize(w_newWidth,w_staticHeight);
   document.applets["xmlEditor"].resize(w_newWidth,w_staticHeight);
   }
   window.scroll(0,0);
}

window.onResize = resize;
window.onLoad = resize;
